import React from "react";
import { useState } from "react";

const Game = () => {
  const [angle, setAngle] = useState("0");
  const [ammo, setAmmo] = useState("3");

  let ammoLeft = ammo;
  let randomNum;

  function startGame() {
    randomNum = Math.floor(Math.random() * 181);
    console.log(randomNum);
    setAmmo(3);
    window.alert("New game has been started, happy shooting!");
    return randomNum;
  }

  function reload() {
    if (ammo > 0) {
      setAmmo(ammo - 1);
    } else {
      startGame();
    }
  }

  function shoot() {
    if(angle>180 || angle<0){
        window.alert("Please enter an angle between 0-180")
    }
    else if(randomNum+20>angle>randomNum-20)
        window.alert
    if (ammo > 0) {
    } else {
      window.alert("Hard luck, restart the game for another shot!");
    }
  }

  const nameTyping = (event) => setAngle(event.target.value);

  return (
    <div>
      <h4>
        Enter an angle between 0°-180° to shoot your missle at enemy submarine.
      </h4>
      <h4>
        If your number is withing the 10 degree range of the enemy sub it will
        be shot down.
      </h4>

      <input
        className="entryField"
        placeholder="Enter Angle"
        onChange={nameTyping}
      />
      <br />

      <button
        className="Button"
        onClick={startGame}
      >{`Start/Reset Game`}</button>

      <br />

      <button
        className="Button"
        onClick={reload}
      >{`Reload (${ammoLeft})`}</button>
      <button onClick={shoot} className="Button">
        Shoot
      </button>
    </div>
  );
};

export default Game;
