import React from "react";
import { useState } from "react";

const Game = () => {
  const [angle, setAngle] = useState("0")
  const [ammo, setAmmo] = useState("3")

  let ammoLeft = ammo;
  let randomNum;

  function startGame() {
    randomNum = Math.floor(Math.random() * 181);
    return randomNum;
  }

  function reload(){
      ammoLeft = ammoLeft-1;
  }


  return (
    <div>
      <h4>Enter an angle between 0°-180° to shoot your missle at enemy submarine.</h4>
      <h4>If your number is withing the 10 degree range of the enemy sub it will be shot down.</h4>

      <input className="entryField" placeholder="Enter Angle" />
      <br />

      <button className="Button" onClick={startGame}>{`START/RESET GAME`}</button>

      <br />

      <button className="Button" onClick={reload}>{`Reload (${ammoLeft})`}</button>
      <button type="submit" className="Button">Shoot</button>
    </div>
  );
};

export default Game;
